   [attributes setObject:likerCount forKey:kHLOfferAttributesLikeCountKey];    [self setAttributes:attributes forOffer:offer];}- (NSDictionary *)attributesForOffer:(OfferModel *)offer {    NSString *key = [self keyForOffer:offer];    return [self.cache objectForKey:key];}- (NSString *)keyForOffer:(OfferModel *)offer {    return [NSString stringWithFormat:@"offer_%@", offer.offerId];}- (void)setAttributes:(NSDictionary *)attributes forOffer:(OfferModel *)offer {    NSString *key = [self keyForOffer:offer];    [self.cache setObject:attributes forKey:key];}- (NSNumber *)likeCountForOffer:(OfferModel *)offer {    NSDictionary *attributes = [self attributesForOffer:offer];    if (attributes) {        return [attributes objectForKey:kHLOfferAttributesLikeCountKey];    }        return [NSNumber numberWithInt:0];}@end